INSERT INTO `lz_item` VALUES (65, 14, 1253083620, NULL, 1253083620, 1, '我校与Sybase公司签署战略合作协议', '<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><font size="3"><span lang="EN-US">2009</span><span style="font-family: 宋体;">年</span><span lang="EN-US">9</span><span style="font-family: 宋体;">月</span><span lang="EN-US">8</span><span style="font-family: 宋体;">日上午，电子科技大学&mdash;</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">战略合作协议签署仪式举行，校党委书记王志强、</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">公司亚太区副总裁兼大中华区总经理邹作基在合作协议上签字并为电子科技大学&mdash;</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">软件工程联合实验室揭牌。杨晓波副校长主持仪式。</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;">&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><font size="3"><span style="font-family: 宋体;">邹作基先生对双方力促战略合作关系的建立表示感谢。他说，在全球一体化的大背景下</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">一直努力开拓中国市场。他希望通过与我校的战略合作，培养更多的专门人才，引领未来发展。他还介绍了</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">公司的研发、产品等情况。</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;">&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><span style="font-family: 宋体;"><font size="3">王志强书记说，联合实验室是一个很好的平台和纽带，对于加强和深化双方合作有着重要推动作用，与企业的紧密联合对学校的人才培养也大有裨益。他对双方的合作前景表示乐观，希望进一步扩大合作，达到双赢。</font></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><span style="font-family: 宋体;"><font size="3">&nbsp;&nbsp;&nbsp; </font><img width="600" vspace="8" height="424" src="http://www.news.uestc.edu.cn/Data/Image/090908/jiepai.jpg" alt="" /></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;">&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><font size="3"><span style="font-family: 宋体;">根据协议规定，</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">将与我校建立联合实验室，捐赠价值</span><span lang="EN-US">100</span><span style="font-family: 宋体;">万美元的实验设备及软件；双方共同开设相关课程、培训教师；设立</span><span lang="EN-US">20</span><span style="font-family: 宋体;">万美元的专项基金；不定期的派专家来校讲座及举办竞赛活动等。</span></font></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;">&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><span style="font-family: 宋体;"><font size="3">邹作基先生同时受聘为我校兼职教授，王志强书记为其颁发聘书并佩戴校徽。邹作基先生感谢学校给他这样一个机会，他表示将为学校发展贡献力量。</font></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;">&nbsp;</p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt; text-indent: 21pt;"><font size="3"><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">公司有关人员，学校办、宣传部、学工部、人事处、教务处和计算机学院等部门负责人参加了战略合作签署仪式。</span></font><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt;"><span lang="EN-US"><o:p><font size="3">&nbsp;</font></o:p></span></p>\r\n<p class="MsoNormal" style="margin: 0cm 0cm 0pt;"><font size="3"><span style="font-family: 宋体;">&nbsp;&nbsp;&nbsp; 链接：</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">是全球领先的企业级和移动软件公司，致力于信息的管理、分析和移动。在所有主要的系统、网络和设备上，是全球公认的在数据密集应用领域有杰出的性能表现的领导者。</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">的信息管理、分析和企业移动解决方案已经为全球的金融服务、通信、制造和政府等的业务关键系统提供强劲动力。</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">于</span><span lang="EN-US">1991</span><span style="font-family: 宋体;">年</span><span lang="EN-US">12</span><span style="font-family: 宋体;">月进入中国，并于</span><span lang="EN-US">1993</span><span style="font-family: 宋体;">年投资</span><span lang="EN-US">230</span><span style="font-family: 宋体;">万美元建立赛贝斯软件</span><span lang="EN-US"> (</span><span style="font-family: 宋体;">中国</span><span lang="EN-US">) </span><span style="font-family: 宋体;">有限公司。</span><span lang="EN-US">Sybase</span><span style="font-family: 宋体;">已陆续在上海、广州、成都等地设立了分公司，在北京、上海、西安建立研发中心，合作伙伴更是遍布全国各地。</span></font></p>', 10, 0, '', '', 1, 'http://www.news.uestc.edu.cn/Data/Image/090908/jiepai.jpg', 1);
